ooms a\n)\nSELECT \n  m25.period_label AS period,\n  m25.revpar AS revpar_q2fy25,\n  m25.trevpar AS trevpar_q2fy25,\n  m25.fnb_revenue AS fnb_revenue_q2fy25,\n  m26.revpar AS revpar_q2fy26,\n  m26.trevpar AS trevpar_q2fy26,\n  m26.fnb_revenue AS fnb_revenue_q2fy26,\n  CASE WHEN m25.revpar IS NOT NULL AND m25.revpar != 0 THEN (m26.revpar - m25.revpar)/m25.revpar ELSE NULL END AS revpar_growth_pct,\n  CASE WHEN m25.trevpar IS NOT NULL AND m25.trevpar != 0 THEN (m26.trevpar - m25.trevpar)/m25.trevpar ELSE NULL END AS trevpar_growth_pct,\n  CASE WHEN m25.fnb_revenue IS NOT NULL AND m25.fnb_revenue != 0 THEN (m26.fnb_revenue - m25.fnb_revenue)/m25.fnb_revenue ELSE NULL END AS fnb_revenue_growth_pct\nFROM metrics m25\nJOIN metrics m26 ON m25.period_label = 'Q2 FY25' AND m26.period_label = 'Q2 FY26'	[{"period": "Q2 FY25", "revpar_q2fy25": 144.08503836317135, "revpar_q2fy26": 415.00108695652176, "trevpar_q2fy25": 980.365537084399, "trevpar_q2fy26": 1063.224232736573, "revpar_growth_pct": 1.880251077218004, "fnb_revenue_q2fy25": 10865720, "fnb_revenue_q2fy26": 8327237, "trevpar_growth_pct": 0.08451816441711646, "fnb_revenue_growth_pct": -0.2336230825016658}]	{"config": {"title": "Raaya Q2 FY25 vs Q2 FY26: RevPAR, TrevPAR, and F&B Revenue with % Growth", "xAxis": "period", "format": {"type": "currency", "decimals": 0}, "series": [{"name": "RevPAR Q2 FY25", "type": "bar", "color": "hsl(var(--chart-1))", "dataKey": "revpar_q2fy25", "yAxisId": "left"}, {"name": "TrevPAR Q2 FY25", "type": "bar", "color": "hsl(var(--chart-2))", "dataKey": "trevpar_q2fy25", "yAxisId": "left"}, {"name": "F&B Revenue Q2 FY25", "type": "bar", "color": "hsl(var(--chart-3))", "dataKey": "fnb_revenue_q2fy25", "yAxisId": "left"}, {"name": "RevPAR Q2 FY26", "type": "bar", "color": "hsl(var(--chart-4))", "dataKey": "revpar_q2fy26", "yAxisId": "left"}, {"name": "TrevPAR Q2 FY26", "type": "bar", "color": "hsl(var(--chart-5))", "dataKey": "trevpar_q2fy26", "yAxisId": "left"}, {"name": "F&B Revenue Q2 FY26", "t