ooms * period_days > 0 THEN room_revenue::numeric / (total_available_rooms * period_days) ELSE NULL END AS revpar,\n    CASE WHEN total_available_rooms * period_days > 0 THEN total_revenue::numeric / (total_available_rooms * period_days) ELSE NULL END AS trevpar\n  FROM revenue_agg\n),\nmetrics_pivot AS (\n  SELECT \n    m.asset_name,\n    MAX(CASE WHEN m.period_label = 'Q2 FY25' THEN m.revpar END) AS revpar_q2fy25,\n    MAX(CASE WHEN m.period_label = 'Q2 FY26' THEN m.revpar END) AS revpar_q2fy26,\n    MAX(CASE WHEN m.period_label = 'Q2 FY25' THEN m.trevpar END) AS trevpar_q2fy25,\n    MAX(CASE WHEN m.period_label = 'Q2 FY26' THEN m.trevpar END) AS trevpar_q2fy26,\n    MAX(CASE WHEN m.period_label = 'Q2 FY25' THEN m.fnb_revenue END) AS fnb_revenue_q2fy25,\n    MAX(CASE WHEN m.period_label = 'Q2 FY26' THEN m.fnb_revenue END) AS fnb_revenue_q2fy26\n  FROM metrics m\n  GROUP BY m.asset_name\n)\nSELECT \n  asset_name,\n  revpar_q2fy25,\n  revpar_q2fy26,\n  (revpar_q2fy26 - revpar_q2fy25) AS revpar_abs_growth,\n  CASE WHEN revpar_q2fy25 != 0 AND revpar_q2fy25 IS NOT NULL THEN (revpar_q2fy26 - revpar_q2fy25) / revpar_q2fy25 ELSE NULL END AS revpar_pct_growth,\n  trevpar_q2fy25,\n  trevpar_q2fy26,\n  (trevpar_q2fy26 - trevpar_q2fy25) AS trevpar_abs_growth,\n  CASE WHEN trevpar_q2fy25 != 0 AND trevpar_q2fy25 IS NOT NULL THEN (trevpar_q2fy26 - trevpar_q2fy25) / trevpar_q2fy25 ELSE NULL END AS trevpar_pct_growth,\n  fnb_revenue_q2fy25,\n  fnb_revenue_q2fy26,\n  (fnb_revenue_q2fy26 - fnb_revenue_q2fy25) AS fnb_revenue_abs_growth,\n  CASE WHEN fnb_revenue_q2fy25 != 0 AND fnb_revenue_q2fy25 IS NOT NULL THEN (fnb_revenue_q2fy26 - fnb_revenue_q2fy25) / fnb_revenue_q2fy25 ELSE NULL END AS fnb_revenue_pct_growth\nFROM metrics_pivot\nORDER BY asset_name	[{"asset_name": "JW Marriott Pune", "revpar_q2fy25": 4.985717896865521, "revpar_q2fy26": 5.4179979777553084, "trevpar_q2fy25": 12.496309403437817, "trevpar_q2fy26": 14.722194135490394, "revpar_abs_growth": 0.4322800808897877, "revpar_