ba8ae2cf-d814-4f79-85c9-898444b54f74	a05060d9-9b81-4057-8bc3-1a49433bfa73	bar	Occupancy rates Q2 across hotels	WITH base AS (\n    SELECT\n        f.hotel_name,\n        DATE_PART('year', f.period_start_date) AS fy,\n        DATE_TRUNC('month', f.period_start_date)::date AS month_start,\n        SUM(f.room_night_sold) AS monthly_rns,\n\n        -- Available room nights per month = rooms * days_in_month from FX table\n        SUM(m.number_of_rooms * fx.days_in_month) AS monthly_arn\n    FROM clean_hotel_financials f\n    JOIN clean_hotel_master m USING (hotel_name)\n    JOIN clean_currency_exchange_rates fx\n        ON fx.month_start_date = DATE_TRUNC('month', f.period_start_date)\n    WHERE EXTRACT(MONTH FROM f.period_start_date) IN (7,8,9)    -- Q2\n    GROUP BY \n        f.hotel_name, \n        fy,\n        month_start\n),\n\nq2 AS (\n    SELECT\n        hotel_name,\n        fy,\n        SUM(monthly_rns) AS qtr_rns,\n        SUM(monthly_arn) AS qtr_arn,\n        SUM(monthly_rns)::numeric / NULLIF(SUM(monthly_arn),0) AS qtr_occ\n    FROM base\n    GROUP BY hotel_name, fy\n),\n\npivot AS (\n    SELECT\n        hotel_name,\n        MAX(CASE WHEN fy = 2025 THEN qtr_occ END) AS occ_q2_fy26,\n        MAX(CASE WHEN fy = 2024 THEN qtr_occ END) AS occ_q2_fy25\n    FROM q2\n    GROUP BY hotel_name\n)\n\nSELECT\n    hotel_name,\n    occ_q2_fy26 * 100 AS occ_q2_fy26_pct,\n    occ_q2_fy25 * 100 AS occ_q2_fy25_pct,\n    (occ_q2_fy26 - occ_q2_fy25)*100 AS pct_change\nFROM pivot\nORDER BY hotel_name\n	{"xKey": "hotel_name", "title": "Quarterly Occupancy", "xAxis": "hotel_name", "yAxes": [{"id": "left", "type": "absolute", "label": "", "scale": "auto", "format": "number", "decimals": 0}, {"id": "right", "type": "absolute", "label": "", "scale": "auto", "format": "number", "decimals": 0}], "yAxis": "", "series": [{"id": "series_1765524088286", "name": "occ_q2_fy25_pct", "type": "bar", "color": "hsl(var(--chart-1))", "label": "", "dataKey": "occ_q2_fy25_pct", "yAxisId": "left", "met