95c0ea1d-1305-4618-85d0-adb9c3efe26e	a05060d9-9b81-4057-8bc3-1a49433bfa73	bar	Reported EBITDA Q2 across hotels	WITH base AS (\n    SELECT\n        f.hotel_name,\n        DATE_PART('year', f.period_start_date) AS fy,\n        DATE_TRUNC('month', f.period_start_date)::date AS month_start,\n\n       \n        CASE \n            WHEN f.reporting_currency = 'INR' THEN f.reported_ebitda\n            WHEN f.reporting_currency = 'USD' THEN f.reported_ebitda * fx.average_exchange_rate\n        END AS reported_ebitda_in_inr,\n\n        -- Monthly available room nights = number_of_rooms * days_in_month\n        (m.number_of_rooms * fx.days_in_month) AS monthly_arn\n    FROM clean_hotel_financials f\n    JOIN clean_hotel_master m USING (hotel_name)\n    JOIN clean_currency_exchange_rates fx\n        ON fx.month_start_date = DATE_TRUNC('month', f.period_start_date)\n    WHERE EXTRACT(MONTH FROM f.period_start_date) IN (7,8,9)   \n),\n\nmonthly AS (\n    SELECT\n        hotel_name,\n        fy,\n        month_start,\n        SUM(reported_ebitda_in_inr) AS monthly_rev_in_inr,\n        SUM(monthly_arn) AS monthly_arn_total\n    FROM base\n    GROUP BY hotel_name, fy, month_start\n),\n\nq2 AS (\n    SELECT\n        hotel_name,\n        fy,\n        SUM(monthly_rev_in_inr) AS qtr_rev_in_inr,\n        SUM(monthly_arn_total) AS qtr_arn\n    FROM monthly\n    GROUP BY hotel_name, fy\n),\n\npivot AS (\n    SELECT\n        hotel_name,\n        MAX(CASE WHEN fy = 2025 THEN qtr_rev_in_inr END) AS ebitda_q2_fy26,\n        MAX(CASE WHEN fy = 2024 THEN qtr_rev_in_inr END) AS ebitda_q2_fy25\n    FROM q2\n    GROUP BY hotel_name\n)\n\nSELECT\n    hotel_name,\n   ebitda_q2_fy26,\nebitda_q2_fy25,\n    (ebitda_q2_fy26 - ebitda_q2_fy25) * 100.0 / ebitda_q2_fy25 AS pct_change,\n    'INR' as reporting_currency\nFROM pivot\nORDER BY hotel_name\n	{"xKey": "hotel_name", "title": "Q2 EBITDA across hotels ", "xAxis": "hotel_name", "yAxes": [{"id": "left", "type": "absolute", "label": "", "scale": "crores",