fb8a22f7-5cdb-4d22-820a-bb62dc25e2ae	a05060d9-9b81-4057-8bc3-1a49433bfa73	bar	EBITDA Margin Q2 across hotels	WITH base AS (\n    SELECT\n        f.hotel_name,\n        DATE_PART('year', f.period_start_date) AS fy,\n        DATE_TRUNC('month', f.period_start_date)::date AS month_start,\n\n       \n        CASE \n            WHEN f.reporting_currency = 'INR' THEN f.reported_ebitda\n            WHEN f.reporting_currency = 'USD' THEN f.reported_ebitda * fx.average_exchange_rate\n        END AS reported_ebitda_in_inr,\n\n\n        CASE \n            WHEN f.reporting_currency = 'INR' THEN f.total_revenue\n            WHEN f.reporting_currency = 'USD' THEN f.total_revenue * fx.average_exchange_rate\n        END AS total_revenue_in_inr,\n\n        -- Monthly available room nights = number_of_rooms * days_in_month\n        (m.number_of_rooms * fx.days_in_month) AS monthly_arn\n    FROM clean_hotel_financials f\n    JOIN clean_hotel_master m USING (hotel_name)\n    JOIN clean_currency_exchange_rates fx\n        ON fx.month_start_date = DATE_TRUNC('month', f.period_start_date)\n    WHERE EXTRACT(MONTH FROM f.period_start_date) IN (7,8,9)   \n),\n\nmonthly AS (\n    SELECT\n        hotel_name,\n        fy,\n        month_start,\n        SUM(reported_ebitda_in_inr) AS monthly_ebitda_in_inr,\n        SUM(total_revenue_in_inr) AS monthly_revenue_in_inr,\n        SUM(monthly_arn) AS monthly_arn_total\n    FROM base\n    GROUP BY hotel_name, fy, month_start\n),\n\nq2 AS (\n    SELECT\n        hotel_name,\n        fy,\n        SUM(monthly_revenue_in_inr) AS qtr_rev_in_inr,\n        SUM(monthly_ebitda_in_inr) AS qtr_ebitda_in_inr,\n        SUM(monthly_ebitda_in_inr)*100 /SUM(monthly_revenue_in_inr) as qtr_ebitda_margin,\n        SUM(monthly_arn_total) AS qtr_arn\n    FROM monthly\n    GROUP BY hotel_name, fy\n),\n\npivot AS (\n    SELECT\n        hotel_name,\n        MAX(CASE WHEN fy = 2025 THEN qtr_ebitda_margin END) AS ebitda_margin_q2_fy26,\n        MAX(CASE WHEN fy = 2024 THEN qtr_ebitda_margin END) AS ebitda_margin_q2_fy25\n    FROM q2\n    GROUP BY hotel_name\n)\n\nSELECT\n    hotel_name,\n   ebitda_margin_q2_fy26,\nebitda_margin_q2_fy25,\n    (ebitda_margin_q2_fy26 - ebitda_margin_q2_fy25) AS pct_change,\n    'INR' as reporting_currency\nFROM pivot\nORDER BY hotel_name\n	{"xKey": "hotel_name", "title": "EBITDA Margin Q2", "xAxis": "hotel_name", "yAxes": [{"id": "left", "type": "absolute", "label": "", "scale": "auto", "format": "number", "decimals": 0}, {"id": "right", "type": "absolute", "label": "", "scale": "auto", "format": "number", "decimals": 0}], "yAxis": "", "series": [{"id": "series_1765526869470", "name": "ebitda_margin_q2_fy25", "type": "bar", "color": "hsl(var(--chart-1))", "label": "", "dataKey": "ebitda_margin_q2_fy25", "yAxisId": "left", "metric_name": "ebitda_margin_q2_fy25"}, {"id": "series_1765526875719", "name": "ebitda_margin_q2_fy26", "type": "bar", "color": "hsl(var(--chart-2))", "label": "", "dataKey": "ebitda_margin_q2_fy26", "yAxisId": "left", "metric_name": "ebitda_margin_q2_fy26"}, {"id": "series_1765526902204", "name": "pct_change", "type": "line", "color": "hsl(var(--chart-3))", "label": "", "dataKey": "pct_change", "yAxisId": "right", "metric_name": "pct_change"}], "xLabel": "", "chartType": "bar", "xAxisType": "category", "lastResult": [{"hotel_name": "Aloft Bengaluru Outer Ring Road", "pct_change": -0.359514843786833, "reporting_currency": "INR", "ebitda_margin_q2_fy25": 39.3203452094676, "ebitda_margin_q2_fy26": 38.9608303656807}, {"hotel_name": "Aloft Bengaluru Whitefield", "pct_change": -0.57475962620844, "reporting_currency": "INR", "ebitda_margin_q2_fy25": 42.2360810566833, "ebitda_margin_q2_fy26": 41.6613214304749}, {"hotel_name": "Anantara Maldives", "pct_change": -1.85959525785893, "reporting_currency": "INR", "ebitda_margin_q2_fy25": 39.930339562742, "ebitda_margin_q2_fy26": 38.0707443048831}, {"hotel_name": "Conrad Maldives Rangali Island", "pct_change": -2.17103096076521, "reporting_currency": "