20251031140903	{"-- Create chat_sessions table\nCREATE TABLE IF NOT EXISTS public.chat_sessions (\n  id UUID NOT NULL DEFAULT gen_random_uuid() PRIMARY KEY,\n  user_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,\n  title TEXT NOT NULL DEFAULT 'New Chat',\n  created_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT now(),\n  updated_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT now()\n);\n\n-- Create chat_messages table\nCREATE TABLE IF NOT EXISTS public.chat_messages (\n  id UUID NOT NULL DEFAULT gen_random_uuid() PRIMARY KEY,\n  session_id UUID NOT NULL REFERENCES public.chat_sessions(id) ON DELETE CASCADE,\n  role TEXT NOT NULL CHECK (role IN ('user', 'assistant', 'system')),\n  content TEXT NOT NULL,\n  sql_query TEXT,\n  query_result JSONB,\n  chart_suggestion JSONB,\n  created_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT now()\n);\n\n-- Enable Row Level Security\nALTER TABLE public.chat_sessions ENABLE ROW LEVEL SECURITY;\nALTER TABLE public.chat_messages ENABLE ROW LEVEL SECURITY;\n\n-- RLS Policies for chat_sessions\nCREATE POLICY \\"Users can view their own chat sessions\\"\n  ON public.chat_sessions\n  FOR SELECT\n  USING (auth.uid() = user_id);\n\nCREATE POLICY \\"Users can create their own chat sessions\\"\n  ON public.chat_sessions\n  FOR INSERT\n  WITH CHECK (auth.uid() = user_id);\n\nCREATE POLICY \\"Users can update their own chat sessions\\"\n  ON public.chat_sessions\n  FOR UPDATE\n  USING (auth.uid() = user_id);\n\nCREATE POLICY \\"Users can delete their own chat sessions\\"\n  ON public.chat_sessions\n  FOR DELETE\n  USING (auth.uid() = user_id);\n\n-- RLS Policies for chat_messages\nCREATE POLICY \\"Users can view messages in their sessions\\"\n  ON public.chat_messages\n  FOR SELECT\n  USING (\n    EXISTS (\n      SELECT 1 FROM public.chat_sessions\n      WHERE chat_sessions.id = chat_messages.session_id\n      AND chat_sessions.user_id = auth.uid()\n    )\n  );\n\nCREATE POLICY \\"Users can create messages in their sessions\\"\n  ON public.chat_messages\n  FOR INSERT\n  WITH CHECK (\n    EXISTS (\n      SELECT 1 FROM public.chat_sessions\n      WHERE chat_sessions.id = chat_messages.session_id\n      AND chat_sessions.user_id = auth.uid()\n    )\n  );\n\n-- Create indexes for better performance\nCREATE INDEX IF NOT EXISTS idx_chat_sessions_user_id ON public.chat_sessions(user_id);\nCREATE INDEX IF NOT EXISTS idx_chat_messages_session_id ON public.chat_messages(session_id);\nCREATE INDEX IF NOT EXISTS idx_chat_messages_created_at ON public.chat_messages(created_at);\n\n-- Create updated_at trigger function\nCREATE OR REPLACE FUNCTION public.update_chat_sessions_updated_at()\nRETURNS TRIGGER AS $$\nBEGIN\n  NEW.updated_at = now();\n  RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql;\n\n-- Create trigger for automatic timestamp updates\nDROP TRIGGER IF EXISTS update_chat_sessions_updated_at ON public.chat_sessions;\nCREATE TRIGGER update_chat_sessions_updated_at\n  BEFORE UPDATE ON public.chat_sessions\n  FOR EACH ROW\n  EXECUTE FUNCTION public.update_chat_sessions_updated_at();"}		nnewprod@gmail.com	\N
20251031142836	{"-- Create the execute_safe_query function for safe SQL execution\nCREATE OR REPLACE FUNCTION public.execute_safe_query(query_text TEXT)\nRETURNS JSONB\nLANGUAGE plpgsql\nSECURITY DEFINER\nSET search_path = public\nAS $$\nDECLARE\n  result JSONB;\n  normalized_query TEXT;\nBEGIN\n  -- Normalize the query for validation (trim and uppercase)\n  normalized_query := UPPER(TRIM(query_text));\n  \n  -- Validate that the query is a SELECT statement\n  IF NOT normalized_query LIKE 'SELECT%' THEN\n    RETURN jsonb_build_object(\n      'error', 'Only SELECT queries are allowed',\n      'details', 'Query must start with SELECT'\n    );\n  END IF;\n  \n  -- Check for dangerous keywords that could modify data\n  IF normalized_query ~ '(DELETE|INSERT|UPDATE|DROP|ALTER|CREATE|TRUNCATE|GRANT|REVOKE)' THEN\n    RETURN jsonb_build_object(\n      'error', 'Query contains forbidden operations',\n      'details', 'Only SELECT queries are permitted'\n    );\n  END IF;\n  \n  -- Execute the query and convert results to JSON\n  BEGIN\n    EXECUTE format('SELECT jsonb_agg(row_to_json(t)) FROM (%s) t', query_text) INTO result;\n    \n    -- Handle empty results\n    IF result IS NULL THEN\n      result := '[]'::jsonb;\n    END IF;\n    \n    RETURN result;\n  EXCEPTION\n    WHEN OTHERS THEN\n      RETURN jsonb_build_object(\n        'error', 'Query execution failed',\n        'details', SQLERRM\n      );\n  END;\nEND;\n$$;\n\n-- Grant execute permission to authenticated users\nGRANT EXECUTE ON FUNCTION public.execute_safe_query(TEXT) TO authenticated;\n\n-- Add comment for documentation\nCOMMENT ON FUNCTION public.execute_safe_query(TEXT) IS 'Safely executes SELECT-only SQL queries and returns results as JSONB. Used by AI orchestrator for dynamic data querying.';"}		nnewprod@gmail.com	\N
20251031155630	{"-- Create profiles table\nCREATE TABLE public.profiles (\n  id uuid PRIMARY KEY REFERENCES auth.users(id) ON DELETE CASCADE,\n  email text,\n  full_name text,\n  created_at timestamp with time zone DEFAULT now(),\n  updated_at timestamp with time zone DEFAULT now()\n);\n\n-- Enable RLS\nALTER TABLE public.profiles ENABLE ROW LEVEL SECURITY;\n\n-- RLS Policies for profiles\nCREATE POLICY \\"Users can view their own profile\\"\n  ON public.profiles\n  FOR SELECT\n  TO authenticated\n  USING (auth.uid() = id);\n\nCREATE POLICY \\"Users can update their own profile\\"\n  ON public.profiles\n  FOR UPDATE\n  TO authenticated\n  USING (auth.uid() = id);\n\n-- Function to handle new user creation\nCREATE OR REPLACE FUNCTION public.handle_new_user()\nRETURNS trigger\nLANGUAGE plpgsql\nSECURITY DEFINER\nSET search_path = public\nAS $$\nBEGIN\n  INSERT INTO public.profiles (id, email, full_name)\n  VALUES (\n    NEW.id,\n    NEW.email,\n    NEW.raw_user_meta_data->>'full_name'\n  );\n  RETURN NEW;\nEND;\n$$;\n\n-- Trigger to create profile on signup\nCREATE TRIGGER on_auth_user_created\n  AFTER INSERT ON auth.users\n  FOR EACH ROW\n  EXECUTE FUNCTION public.handle_new_user();"}		nnewprod@gmail.com	\N
