20251102192118	{"-- Create function to sanitize table names (prevent SQL injection)\nCREATE OR REPLACE FUNCTION public.sanitize_table_name(name TEXT)\nRETURNS TEXT\nLANGUAGE plpgsql\nIMMUTABLE\nAS $$\nBEGIN\n  -- Convert to lowercase, replace spaces with underscores, remove special chars\n  RETURN regexp_replace(\n    regexp_replace(lower(trim(name)), '[^a-z0-9_]', '_', 'g'),\n    '_+', '_', 'g'\n  );\nEND;\n$$;\n\n-- Create function to get table columns (for AI query generation)\nCREATE OR REPLACE FUNCTION public.get_table_columns(table_name TEXT)\nRETURNS TABLE (\n  column_name TEXT,\n  data_type TEXT,\n  is_nullable TEXT\n) \nLANGUAGE plpgsql\nSECURITY DEFINER\nSET search_path = public\nAS $$\nBEGIN\n  -- Validate table name exists and user has access\n  IF NOT EXISTS (\n    SELECT 1 FROM information_schema.tables \n    WHERE table_schema = 'public' \n    AND table_name = $1\n  ) THEN\n    RAISE EXCEPTION 'Table does not exist: %', $1;\n  END IF;\n\n  RETURN QUERY\n  SELECT \n    c.column_name::TEXT,\n    c.data_type::TEXT,\n    c.is_nullable::TEXT\n  FROM information_schema.columns c\n  WHERE c.table_name = $1\n    AND c.table_schema = 'public'\n    AND c.column_name NOT IN ('id', 'user_id', 'source_workbook', 'source_mapping_id', 'extracted_at');\nEND;\n$$;\n\n-- Create function to execute dynamic DDL (used by manage-schema-table edge function)\nCREATE OR REPLACE FUNCTION public.execute_ddl(ddl_statement TEXT)\nRETURNS JSONB\nLANGUAGE plpgsql\nSECURITY DEFINER\nSET search_path = public\nAS $$\nBEGIN\n  -- Execute the DDL statement\n  EXECUTE ddl_statement;\n  \n  RETURN jsonb_build_object(\n    'success', true,\n    'message', 'DDL executed successfully'\n  );\nEXCEPTION\n  WHEN OTHERS THEN\n    RETURN jsonb_build_object(\n      'success', false,\n      'error', SQLERRM\n    );\nEND;\n$$;"}		nnewprod@gmail.com	\N
